/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react';
import Avatar from 'react-avatar';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Accordion, AccordionItem } from 'react-sanfona';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


      // <Link to={"/edit/"+props.contact._id}>edit</Link> | <a href="#" onClick={() => { props.deletecontact(props.contact._id) }}>delete</a>


export default class ContactsList extends Component {
  constructor(props) {
    super();

    this.state = {
      paginationValue: '50',
      contact: []};
  }


  componentDidMount() {
    axios.get('http://localhost:5000/contacts/')
      .then((response) => { 
        // const contact = response.data.map((contacts) =>({
        //   id: contacts.id,
        //   fullname: contacts.fullname
        // }));
        this.setState({ contact: response.data});
      })
      .catch((error) => {
        console.log(error);
      });
  }


  // ContactList() {
  //   return this.state.contact.map(eachcontact => {
  //     return <li key={eachcontact._id}><Avatar name={eachcontact.fullname} size="25" round={true} />  {eachcontact.fullname}</li>
  //   })
  // }

  render() {
    return (
      <div className ="container">
        <h3 style={{color: "rgb(49, 90, 86)"}}>{this.state.contact.lenght} CONTACTS</h3>
         <Accordion>
        {this.state.contact.map(item => {
          return (
        <AccordionItem  title={`${item.fullname}`} expanded={item === 1}>  

              <div>

                <ul className = "prof">
               <Avatar name={item.fullname} size="35" round={true} />
             <li><FontAwesomeIcon icon="user"/>  {`${item.fullname}`}</li>  
             <li> <FontAwesomeIcon icon="envelope"/>  {`${item.email}`} </li>   
             <li> <FontAwesomeIcon icon="mobile"/>   {`${item.phone}`} </li>  
             <li> <FontAwesomeIcon icon="map-marker-alt"/>    {`${item.address}`} </li>  
                </ul>
              </div>
            </AccordionItem> 
          );
        })}
      </Accordion>      
      </div>
    )
  }
}
